读取文件最后一行
$pos = -2;        //偏移量
$eof = " ";        //行尾标识
$data = "";
while ($line > 0){//逐行遍历
  while ($eof != "\n"){ //不是行尾
    fseek($fp, $pos, SEEK_END);//fseek成功返回0，失败返回-1  
    $eof = fgetc($fp);//读取一个字符并赋给行尾标识
    $pos--;//向前偏移
  }
  $eof = " ";
  $data .= fgets($fp);//读取一行
  $line--;
}

